'use strict';

module.exports = require('./lib/express');
var path = require('path');
var express = require('express');
var app = express();
app.use(express.static(__dirname+'/public'));
app.use(express.static(path.join(__dirname, 'public')));

app.set('view engine', 'jade');
app.get('/',function(req,res){
	res.render('index.jade');
});

app.get('/addEvent',function(req,res){
	res.render('addEvent.jade');
});

app.get('/addEventMap',function(req,res){
	res.render('addEventMap.jade');
});

app.get('/addMap',function(req,res){
	res.render('addMap.jade');
});

app.get('/dashboard',function(req,res){
	res.render('dashboard.jade');
});

app.get('/findEvents',function(req,res){
	res.render('findEvents.jade');
});

app.get('/pfindEvents',function(req,res){
	res.render('pfindEvents.jade');
});

app.get('/register',function(req,res){
	res.render('register.jade');
});
app.listen(3000, function () {
  console.log('Example app listening on port 3000!');
});
