$(function() {
  $( "#newEvent" ).click(function () {
    var event_name = document.getElementById('event_name').value;
    var event_description = document.getElementById('event_description').value;
    var event_address = document.getElementById('event_address').value;
    var event_date =  document.getElementById('event_date').value;
    var event_time =  document.getElementById('event_time').value;
    var activity_type =  document.getElementById('activity_type').value;
    var ref = new Firebase("https://toitle.firebaseio.com");
    if (event_name != null && event_address != null && event_description != null && activity_type != null){
      ref.child('events').child(event_name).set({
        event_name: event_name, 
        event_address: event_address,
        event_description: event_description,
        activity_type: activity_type,
        event_date: event_date,
        event_time: event_time,
        user_id: authData.uid
      });
      alert("Event successfully added!");
      window.location = "findEvents.html";
    } else {
      alert("You need to complete the entire form.");
    }
  });
});