function getParameterByName(name) {
  name = name.replace(/[\[]/, "\\[").replace(/[\]]/, "\\]");
  var regex = new RegExp("[\\?&]" + name + "=([^&#]*)"),
  results = regex.exec(location.search);
  return results === null ? "" : decodeURIComponent(results[1].replace(/\+/g, " "));
}
var event_location=getParameterByName('location');
      //var event_location
      $(function() {
        event_location=getParameterByName('location');
        
        $( "#Add" ).click(function () {
          var event_name = $('#event_name').val();
          event_address = event_location;
          var event_description = $('#event_description').val();
          var event_date = $('#event_date').val();
          var event_time = $('#event_time').val();
          var activity_type = $('#activity_type').val();
          var ref = new Firebase("https://toitle.firebaseio.com");
          if (event_name == "" || event_description == "" || activity_type == "" || event_date == "" || event_time == ""){
            alert("You need to complete the entire form.");
          } else {
            ref.child('events').child(event_name).set({
              event_name: event_name, 
              event_address: event_address,
              event_description: event_description,
              event_date: event_date,
              event_time: event_time,
              activity_type: activity_type
            });
            alert("Submitted " + event_description);    
            window.location="findEvents.html";

          }
        });
      });